client_script:
frappe.web_form.after_load = () => {
    if (frappe.web_form.doc.events) {
        frappe.call({
			method: 'solve_ninja.api.events.get_action_html',
			args: {
				"action": frappe.web_form.doc.events
			},
			callback: function(r) {
			    console.log(r)
				if (!r.exc) {
					let container = $("#action_html")
					if (container) {
					    container.html(r.message)
					}
				}
			},
			freeze: true,
			freeze_message: __('...fetching Action')
		});
    }
}

